mapper.insert:
insert into board_tb (
	board_id
	, board_title
	, board_content
	, user_id
	, board_attached
	, boardFolder_id
	
)
values (
	board_id_seq.nextval
	, #{board_title}
	, #{board_content}
	, #{user_id}
	, #{board_attached}
	, #{boardFolder_id}
	
)

insert into boardAttached_tb (
	bAttached_id
	, board_id
	, bAttached_saved
	, bAttached_original	
)
values (
	bAttached_id_seq.nextval
	, #{board_id}
	, #{bAttached_saved}
	, #{bAttached_original}
	
)

mapper.select:
select
	*
from
	board_tb
where
	board_title = #{board_title}
	and board_content = #{board_content}
	and user_id = #{user_id}
	and board_attached = #{board_attached}
	and boardFolder_id = #{boardFolder_id}

select count(*) from board_tb where
board_title like '%' || #{searchText} || '%' and
boardFolder_id = #{boardFolder_id}

mapper.delete:
delete
from
	board_tb
where
	board_id = #{board_id}
	and user_id = #{user_id}

delete
from
	boardAttached_tb
where
	board_id = #{board_id}
	and bAttached_id = #{bAttached_id}

mapper.update:
update
	board_tb
set
	board_title = #{board_title}
	, board_content = #{board_content}
	, board_attached = #{board_attached}
where
	board_id = #{board_id}
	and user_id = #{user_id}

