mapper.insert:
insert into board_tb (
	board_id
	, board_title
	, board_content
	, user_id
	, board_attached
	, boardfolder_id
	
)
values (
	board_id_seq.nextval
	, #{board_title}
	, #{board_content}
	, #{user_id}
	, #{board_attached}
	, #{boardfolder_id}
	
)

insert into boardAttached_tb (
	bAttached_id
	, board_id
	, bAttached_saved
	, bAttached_original	
)
values (
	boardAttached_id_seq.nextval
	, #{board_id}
	, #{bAttached_saved}
	, #{bAttached_original}
	
)

mapper.delete:
delete
from
	board_tb
where
	board_id = #{board_id}
	and user_id = #{user_id}

mapper.update:
update
	board_tb
set
	board_title = #{board_title}
	, board_content = #{board_content}
	
	, board_attached = #{board_attached}
	
where
	board_id = #{board_id}
	and user_id = #{user_id}
